function MarkMessagesAsRead(IDUser) { try { $.ajax({ dataType: "json", contentType: "application/json; charset=utf-8", type: "POST", url: "http://" + WebServicesPath + "/Message/MessageManager.asmx/MarkAllConversationMessagesAsViewed", data: "{ IDUserConversationOwner: '" + IDUser + "'}", success: function (msg) { $("#lblCountMessages").html(''); $("#lblCountMessages").removeClass("lblCountMessagesOn"); $("#lblCountMessages").addClass("lblCountMessagesOff") }, error: function (msg) { console.log(msg.status + ' ' + msg.statusText) } }) } catch (err) { } }